def check_and_sum(digits):
    number = sum(digits)
    """–°–≤–æ–¥–∏—Ç —á–∏—Å–ª–∞ –∫ –º–æ–Ω–∞–¥–µ –æ—Ç 1 –¥–æ 9"""
    while number > 9:
        number = sum(map(int, str(number)))
    return number


def calculate_chakras(date_str):
    # –£–¥–∞–ª—è–µ–º –≤—Ä–µ–º—è, –µ—Å–ª–∏ –µ—Å—Ç—å, –∏ –ø–æ–ª—É—á–∞–µ–º —Ç–æ–ª—å–∫–æ –¥–∞—Ç—É
    date_part = date_str.split()[0]
    day, month, year = map(int, date_part.split('.'))
    month_names = [
        "–ù–∞–∑–≤–∞–Ω–∏–µ –º–µ—Å—è—Ü–∞", "–Ø–Ω–≤–∞—Ä—å", "–§–µ–≤—Ä–∞–ª—å", "–ú–∞—Ä—Ç", "–ê–ø—Ä–µ–ª—å", "–ú–∞–π", "–ò—é–Ω—å",
        "–ò—é–ª—å", "–ê–≤–≥—É—Å—Ç", "–°–µ–Ω—Ç—è–±—Ä—å", "–û–∫—Ç—è–±—Ä—å", "–ù–æ—è–±—Ä—å", "–î–µ–∫–∞–±—Ä—å"
    ]
    month_name = month_names[month]
    # –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–Ω–µ–π –≤ –º–µ—Å—è—Ü–µ (—É—á–∏—Ç—ã–≤–∞—è –≤–∏—Å–æ–∫–æ—Å–Ω—ã–π –≥–æ–¥ –¥–ª—è —Ñ–µ–≤—Ä–∞–ª—è)
    days_in_month = calendar.monthrange(year, month)[1]

    # –°–æ–∑–¥–∞–µ–º —Å–ø–∏—Å–æ–∫ –≤—Å–µ—Ö —Ü–∏—Ñ—Ä –¥–∞—Ç—ã
    all_digits = list(map(int, list(f"{day:02d}{month:02d}{year}")))  # –¥–ª—è –º–æ–Ω–∞–¥—ã
    # dm_digits = list(map(int, list(f"{day:02d}{month:02d}")))    # –¥–ª—è —Å—É–º–º—ã —Ä–æ–¥–∞

    monad = check_and_sum(all_digits)
    # family_sum = check_and_sum(dm_digits)
    
    year_markers = db.get_year_markers(year)
    month_markers = db.get_month_markers(month_name, days_in_month)
    day_markers = [days_in_month - day] * 3
    
    sum_contours = [a + b + c for a, b, c in zip(year_markers, month_markers, day_markers)]
    base_markers = [23, 28, 33]
    final_contours = [sc % bm if sc % bm != 0 else bm for sc, bm in zip(sum_contours, base_markers)]
    
    chakras = db.get_chakras(final_contours)
    return monad, chakras
